mapper.select:
select paper.*
from stutopic inner join topicpaper on stutopic.stutopic_id=topicpaper.stutopic_id inner join paper on topicpaper.paper_id=paper.paper_id
where student_id=#{student_id}

select paper.*
stutopic inner join topicpaper on stutopic.stutopic_id=topicpaper.stutopic_id inner join paper on topicpaper.paper_id=paper.paper_id
where student_id=#{student_id} and topic_id=#{topic_id}
